function addToCart(a,t){let e=$.ajax({type:"POST",url:jwdata.base_url+"/ajax/cart/add-to-cart",dataType:"json",async:!1,data:{variant_id:a,quantity:t,_token:$('meta[name="csrf-token"]').attr("content")}}).responseText;return JSON.parse(e)}function removeItemCart(a){let t=$.ajax({type:"DELETE",url:jwdata.base_url+"/ajax/cart/remove-item",dataType:"json",async:!1,data:{variant_id:a}}).responseText;return JSON.parse(t)}function removeCart(){let a=$.ajax({type:"DELETE",url:jwdata.base_url+"/ajax/cart/remove",dataType:"json",async:!1,data:{}}).responseText;return JSON.parse(a)}
